import React, { Component } from 'react';
import { connect } from "react-redux";


class Banner extends Component {

  constructor(params) {

  }

  renderMovie = () => {


    return this.props.listMovie.map((movie, index) => {
      console.log(movie)
      return <li key={index} className="tab__li_2">{movie.tenPhim} </li>;
    });
  };



    render() {
        return (
            <section className="banner">
  <div id="carouselId" className="carousel slide" data-ride="carousel">
    <ol className="carousel-indicators">
      <li data-target="#carouselId" data-slide-to={0} className="active" />
      <li data-target="#carouselId" data-slide-to={1} />
      <li data-target="#carouselId" data-slide-to={2} />
      <li data-target="#carouselId" data-slide-to={3} />
      <li data-target="#carouselId" data-slide-to={4} />
    </ol>
    <div className="carousel-inner" role="listbox">
      <div className="carousel-item active">
        <img src="./img/banner2.jpg" alt />
      </div>
      <div className="carousel-item">
        <img src="./img/banner4.jpg" alt />
      </div>
      <div className="carousel-item">
        <img src="./img/banner3.jpg" alt />
      </div>
      <div className="carousel-item">
        <img src="./img/banner2.jpg" alt />
      </div>
      <div className="carousel-item">
        <img src="./img/banner5.jpg" alt />
      </div>
    </div>
    <a className="carousel-control-prev" href="#carouselId" role="button" data-slide="prev">
      <span className="carousel-control-prev-icon" aria-hidden="true" />
      <span className="sr-only">Previous</span>
    </a>
    <a className="carousel-control-next" href="#carouselId" role="button" data-slide="next">
      <span className="carousel-control-next-icon" aria-hidden="true" />
      <span className="sr-only">Next</span>
    </a>



    <div className="banner__list">
      <ul className="tab__ul_1">
        <li className="tab__li_1">Phim<i className="fa fa-angle-down" />
          <ul className="tab__ul_2">
            {this.renderMovie()}
          </ul>
        </li>
        <li className="tab__li_1">Rạp<i className="fa fa-angle-down" /></li>
        <li className="tab__li_1">Ngày xem<i className="fa fa-angle-down" /></li>
        <li className="tab__li_1">Suất chiếu<i className="fa fa-angle-down" /></li>
        <li className="tab__li_1"><button className="btn ">MUA VÉ NGAY</button></li>
      </ul>
    </div>



  </div>
</section>

        )
    }
}

const mapStateToProps = state => {
  return {
    listMovie: state.movieReducer.listMovie
  };
};


export default connect(mapStateToProps, null)(Banner);
